
	

#include <stdio.h>

#include "omp.h"

void oddEvenSortParallel(int arr[], int size){
	int phase, i, temp;
	double start = omp_get_wtime();
	for(phase = 0; phase<size; ++phase){
		if(!(phase%2)){
			//even
			#pragma omp parallel for num_threads(4) default(none) shared(arr, size) private(i, temp)
			for(i=0; i<size-1; i+=2){
				if(arr[i] > arr[i+1]){
					temp = arr[i];
					arr[i] = arr[i+1];
					arr[i+1] = temp;
				}
			}
		}
		else{
			//odd
			#pragma omp parallel for num_threads(4) default(none) shared(arr, size) private(i, temp)
			for(i=1; i<size-1; i+=2){
				if(arr[i] > arr[i+1]){
					temp = arr[i];
					arr[i] = arr[i+1];
					arr[i+1] = temp;
				}
			}
		}
		// printf("In Iteration %d: ", phase);
		// for(i=0; i<size; ++i)
		// 	printf("%d ", arr[i]);
		// printf("\n");
	}
	double stop = omp_get_wtime();
	printf("%lf", (stop-start));
}

